package day03;

public class VariableDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

		//approach - C
		int s=100,q=200,r=300;
		
		
		System.out.println("The Value of variable s is = "+s);
		System.out.println("The Value of variable q is : "+q);
		System.out.println("r= "+r);
		
		//print on same line
		
		System.out.println("s="+s+" q="+q+" r="+r);
		System.out.println(s+q+r);
		
		String a="Hello";
		String b="Hi";
		System.out.println(a+" "+b);
		System.out.println(" "+a+" "+b);
		
		
		//print values of whole numbers
		
		byte d=123;
		System.out.println(d);
		
		short t=3838;
		System.out.println(t);
		
		long l=1234567890000899912L;
		System.out.println(l);
		
		//Decimal Numbers -- float, double
		
		float price=45.8778f;
		System.out.println(price);
		
		
		double u=45678.7856789;
		System.out.println(u);
		
		
		//character and string
		char grade='f';
		System.out.println(grade);
		
		/*
		char ch='XYZ';		//invalid
		String ch="XYZ";	//valid
		char ch='A';		//valid
		String ch="A";		//valid
		*/
		
		String name="Vinay";
		System.out.println(name);
		
		
		//boolean
		
		boolean y=false; 		//either true or false
		System.out.println(y);
		
		/*
		boolean j="false";		//invalid
		String k="false";		//valid
		*/
		
		
		
		
		
		
		
	}

}
